						CALL MANTENIMIENTO_CUENTA (@CUE_Codigo,NULL,NULL,CPP_Codigo,MONED_Codigo,CPC_total,CPC_Fecha,@CUE_FlagEstadoPago,@CUE_FechaCanc,COMPP_Codigo,NOW(),NULL,1, 1, NULL, NULL, NULL, NULL, NULL);
						
