					SET @PRODUNIC_flagPrincipal='';
					SET @PRODUNIC_Factor='';
					SELECT CPU.PRODUNIC_flagPrincipal,CPU.PRODUNIC_Factor INTO @PRODUNIC_flagPrincipal,@PRODUNIC_Factor
