						CALL MANTENIMIENTO_ALMACENPRODUCTO(@ALMPROD_Codigo,D_ALMAP_Codigo,D_PROD_Codigo,COMPP_Codigo,@CANTIDADTOTAL,@COSTOPROMEDIO,NOW(),'',1,NULL,NULL,NULL);
						
