(505, 1, 'select c.CPP_Codigo, c.CLIP_Codigo as CCLIENTE,\nc.CPC_total as TOTAL, c.CPC_total  as MONTOLETRA , c.CPC_igv as IGV, c.CPC_subtotal as SUBTOTAL, c.CPC_Fecha as FECHAEMI, c.FORPAP_Codigo, c.MONED_Codigo, CONCAT (c.CPC_Serie,'' '',c.CPC_Numero) as COMPROBANTE\n from cji_comprobante c where CPP_Codigo=$CodigoPrincipal', '', 14, '$CodigoPrincipal', ''),
(506, 2, 'SELECT c.CPC_TipoOperacion, c.CLIP_Codigo, c.PROVP_Codigo , CASE c.CPC_TipoOperacion WHEN ''V'' THEN ( SELECT CASE cl.EMPRP_Codigo WHEN 0 THEN (SELECT CONCAT (pe.PERSC_Nombre,'' '',pe.PERSC_ApellidoPaterno,'' '',pe.PERSC_ApellidoMaterno) FROM cji_persona pe WHERE pe.PERSP_Codigo = cl.PERSP_Codigo ) ELSE (SELECT emp.EMPRC_RazonSocial FROM cji_empresa emp WHERE emp.EMPRP_Codigo=cl.EMPRP_Codigo) END FROM cji_cliente cl WHERE cl.CLIP_Codigo=c.CLIP_Codigo ) ELSE ( SELECT CASE pro.PROVP_Codigo WHEN 0 THEN (SELECT CONCAT (pe.PERSC_Nombre,'' '',pe.PERSC_ApellidoPaterno,'' '',pe.PERSC_ApellidoMaterno) FROM cji_persona pe WHERE pe.PERSP_Codigo = pro.PROVP_Codigo ) ELSE (SELECT emp.EMPRC_RazonSocial FROM cji_empresa emp WHERE emp.EMPRP_Codigo=pro.EMPRP_Codigo) END FROM cji_proveedor pro WHERE pro.PROVP_Codigo = c.PROVP_Codigo ) END as NOMBRE, CASE c.CPC_TipoOperacion WHEN ''V'' THEN ( SELECT CASE cl.EMPRP_Codigo WHEN 0 THEN (SELECT pe.PERSC_Direccion FROM cji_persona pe WHERE pe.PERSP_Codigo = cl.PERSP_Codigo ) ELSE (SELECT emp.EMPRC_Direccion FROM cji_empresa emp WHERE emp.EMPRP_Codigo=cl.EMPRP_Codigo) END FROM cji_cliente cl WHERE cl.CLIP_Codigo=c.CLIP_Codigo ) ELSE ( SELECT CASE pro.EMPRP_Codigo WHEN 0 THEN (SELECT pe.PERSC_Direccion FROM cji_persona pe WHERE pe.PERSP_Codigo = pro.PERSP_Codigo ) ELSE (SELECT emp.EMPRC_Direccion FROM cji_empresa emp WHERE emp.EMPRP_Codigo= pro.EMPRP_Codigo) END FROM cji_proveedor pro WHERE pro.PROVP_Codigo = c.PROVP_Codigo ) END as DIRECCION, CASE c.CPC_TipoOperacion WHEN ''V'' THEN ( SELECT CASE cl.EMPRP_Codigo WHEN 0 THEN (SELECT pe.PERSC_Ruc FROM cji_persona pe WHERE pe.PERSP_Codigo = cl.PERSP_Codigo ) ELSE (SELECT emp.EMPRC_Ruc FROM cji_empresa emp WHERE emp.EMPRP_Codigo=cl.EMPRP_Codigo) END as RUC FROM cji_cliente cl WHERE cl.CLIP_Codigo=c.CLIP_Codigo ) ELSE ( SELECT CASE pro.EMPRP_Codigo WHEN 0 THEN (SELECT pe.PERSC_Ruc FROM cji_persona pe WHERE pe.PERSP_Codigo = pro.PERSP_Codigo ) ELSE (SELECT emp.EMPRC_Ruc FROM cji_empresa emp WHERE emp.EMPRP_Codigo=pro.EMPRP_Codigo) END FROM cji_proveedor pro WHERE pro.PROVP_Codigo = c.PROVP_Codigo )END as RUC FROM cji_comprobante c WHERE c.CPP_Codigo=$ccliente', 'CPP_Codigo', 14, '$ccliente', ''),
(507, 2, 'SELECT und.UNDMED_Descripcion as UNIDAD , cd.CPDEC_Cantidad as CANTIDAD, cd.CPDEC_Total as TOTALP, cd.CPDEC_Pu as PUNIT, cd.CPDEC_Descripcion as NOMBREP, cd.UNDMED_Codigo as UCODIGO FROM cji_comprobantedetalle cd INNER JOIN cji_unidadmedida und ON und.UNDMED_Codigo=cd.UNDMED_Codigo WHERE cd.CPP_Codigo=$ccomp', 'CPP_Codigo', 14, '$ccomp', 'grupo'),
