<table class="tb_detalle">    <thead>    <tr class="cabeceraTabla">        <td style="width: 50px">ITEM</td>        <td style="width: 550px;">ARTICULO</td>        <td style="width: 70px">CANTIDAD</td>        <td style="width: 70px">COSTO</td>        <td style="width: 20px"></td>        <td style="width: 20px"></td>        <td style="width: 20px"></td>    </tr>    </thead>    <tbody>    <?php    if (count($lista) > 0) {        foreach ($lista as $indice => $valor) {            $class = $indice % 2 == 0 ? 'itemParTabla' : 'itemImparTabla';            $display = 'style="display: block"';            $readonly = '';            $color = '';            if ($valor->INVD_FlagActivacion == 1) {                $display = 'style="display: none"';                $readonly = 'readonly="readonly"';                $color = 'cajaSoloLectura';            }            ?>            <tr id="c_<?php echo $t_indice + 1 ?>" class="<?php echo $class; ?>">                <td>                    <div align="center"><?php echo $t_indice + 1 ?></div>                </td>                <td>                    <div align="left"><?php                        echo $valor->PROD_Nombre . ' ' . $valor->PROD_Presentacion                        ?></div>                </td>                <td>                    <div align="center">                        <input <?php echo $readonly; ?> type="text"                                                        class="cajaPequena cajaPadding detalle_cantidad <?php echo $color; ?>"                                                        value="<?php echo $valor->INVD_Cantidad; ?>">                    </div>                </td>                <td>                    <div align="center">                        <input <?php echo $readonly; ?> type="text"                                                        class="cajaPequena cajaPadding detalle_costo  <?php echo $color; ?>"                                                        value="<?php echo $valor->INVD_Pcosto; ?>">                    </div>                </td>                <td>                    <div <?php echo $display; ?>  align="center">                        <a onclick="modificar_detalle(<?php echo $valor->INVD_Codigo ?>, <?php echo $t_indice + 1 ?>)">                            <img src="<?php echo base_url() ?>images/save.gif" title="Modificar Registro"                                 style="cursor: pointer">                        </a>                    </div>                </td>                <td>                    <div <?php echo $display; ?>  align="center">                        <a onclick="eliminar_detalle(<?php echo $valor->INVD_Codigo ?>)">                            <img src="<?php echo base_url() ?>images/eliminar.png" title="Eliminar Registro"                                 style="cursor: pointer">                        </a>                    </div>                </td>                <td>                    <div <?php echo $display; ?>  align="center">                        <a onclick="generar_movimiento(<?php echo $valor->INVD_Codigo ?>)">                            <img style="width: 20px; cursor: pointer" src="<?php echo base_url() ?>images/Y.png"                                 title="Aprobar (Generar Movimientos)">                        </a>                    </div>                </td>            </tr>            <?php            $t_indice++;        }    } else {        ?>        <tr>            <td colspan="8" width="100%" class="mensaje">No hay ning&uacute;n registro que cumpla con los criterios de b&uacute;squeda</td>        </tr>    <?php    }    ?>    </tbody></table><style>    #pagination_container a {        font-family: Arial;        font-size: 8pt;        cursor: pointer;    }    #pagination_container a:hover {        font-size: 8pt !important;    }</style><div id="pagination_container" class="busqueda_container"     style="text-align: center;width: 100%; margin-top: 15px;"><?php echo $paginacion; ?></div><script>    $(document).ready(function () {        $(".busqueda_container a").each(function () {            var url = $(this).attr("href");            $(this).removeAttr("href");            $(this).attr("pag", url);            $(this).bind("click", function () {                paginacion_jquery(url);            });        })    })</script>